# File path
file = "antennaDesign/ESD611/ant2.txt"

# Initialize separate lists
elev_angles = []
elev_gains = []
az_angles = []
az_gains = []

with open(file, "r") as f:
    next(f)  # Skip first two lines
    next(f)

    for line in f:
        tokens = line.split()
        az = float(tokens[0])  # radians
        el = float(tokens[1])  # radians
        freq = int(tokens[2]) 
        total_gain = float(tokens[7])

        if freq == 5800000000:
            if az == 0:
                elev_angles.append(np.degrees(el))
                elev_gains.append(total_gain)

            if az == 1.5707963267949: #90deg
                az_angles.append(np.degrees(el))
                az_gains.append(total_gain)




