print(f"elevation_deg: {len(elev_angles)}; elevation_db: {len(elev_gains)}; azimuth_deg: {len(az_angles)}; azimuth_db: {len(az_gains)}")

import matplotlib.pyplot as plt

# Create Cartesian plot
plt.figure(figsize=(10, 6))

# Plot elevation cut
plt.plot(elev_angles, elev_gains, label='Elevation Cut (Az = 0)', color='b')

# Plot azimuth cut
plt.plot(az_angles, az_gains, label='Azimuth Cut (Az = 90)', color='r')

# Format plot
plt.title("Radiation Pattern @ 5.8 GHz")
plt.xlabel("Angle (degrees)")
plt.ylabel("Gain (dB)")
plt.grid(True)
plt.legend()
plt.tight_layout()
plt.show()